import React from 'react'
import "./Home.css"; // Import the CSS file

const Home = () => {
  return (
    <div>
 <div className="hero-section">
      <h1 className="hero-title">Welcome to Our Sports Store</h1>
      <p className="hero-subtitle">Performance. Style. Passion for the Game.</p>
      <button className="hero-button">Shop Now</button>
      
      <div id="products"> </div>
      
      
  {/* Your products go here */}
    <h2 className="text-2xl font-bold text-center mt-10 mb-6">Featured Products</h2>
<div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 px-4">
  {[
    {
      name: "Nike Phantom GX",
      price: "$120",
      image: "/images/shoes1.jpg",
    },
    {
      name: "Adidas Predator Elite",
      price: "$135",
      image: "/images/shoes2.jpg",
    },
    {
      name: "Puma Ultra Play",
      price: "$99",
      image: "/images/shoes3.jpg",
    },
    {
      name: "Nike Mercurial Vapor",
      price: "$140",
      image: "/images/shoes4.jpg",
    },
    {
      name: "Adidas Copa Mundial",
      price: "$110",
      image: "/images/shoes5.jpg",
    },
    {
      name: "Mizuno Morelia Neo",
      price: "$130",
      image: "/images/shoes6.jpg",
    },
  ].map((product, i) => (
    <div key={i} className="border rounded-lg shadow-md p-4 text-center hover:shadow-lg transition duration-200">
      <img src={product.image} alt={product.name} className="w-full h-48 object-cover rounded mb-4" />
      <h3 className="text-lg font-semibold">{product.name}</h3>
      <p className="text-blue-600 font-bold mb-2">{product.price}</p>
      <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
        Add to Cart
      </button>
    </div>
  ))}
</div>

</div>
 </div>

     
    )
  }

      export default Home
