import React from 'react';
import './Balls.css';

const balls = [
  // ⚽ Footballs
  {
    id: 1,
    name: 'Adidas UEFA Champions League Pro',
    price: '$60',
    image: 'https://images.footballfanatics.com/adidas/uefa-champions-league-pro-football-white_ss4_p-13373711+u-kt0ufhtmzzgprbbm5ds2+v-ezjzfb2mbtxx8nqcz9kj.jpg'
  },
  {
    id: 2,
    name: 'Puma Orbita La Liga Ball',
    price: '$55',
    image: 'https://images.footballfanatics.com/puma/puma-orbita-la-liga-1-football-2023-24_ss4_p-13378856+u-cnyzsfkwtxqxtmqxfwbp+v-5fupcqwuznp6tzxtxolj.jpg'
  },
  {
    id: 3,
    name: 'Nike Premier League Flight Ball',
    price: '$70',
    image: 'https://images.footballfanatics.com/nike/nike-premier-league-flight-football_ss4_p-12002143+u-ykqgbyvmsq9cxtowb6p1+v-vxyksqzsn9vntg5xai9k.jpg'
  },
  {
    id: 4,
    name: 'Adidas Al Rihla World Cup Ball',
    price: '$65',
    image: 'https://images.footballfanatics.com/adidas/fifa-world-cup-qatar-2022-al-rihla-pro-ball_ss4_p-12001955+u-xjo8bmt1otvpnhmepjsk+v-vo2rfxltblzocedxvpv9.jpg'
  },
  {
    id: 5,
    name: 'Mitre Delta Max FA Cup Ball',
    price: '$50',
    image: 'https://i.ebayimg.com/images/g/XYsAAOSwjv1jvw-4/s-l1600.jpg'
  },
  {
    id: 6,
    name: 'Nike Strike Football',
    price: '$40',
    image: 'https://images.footballfanatics.com/nike/nike-strike-football-white_ss4_p-13370891+u-1y3t2zyupylsnspxvsli+v-2kgwhkhkvt0jlph2lvrg.jpg'
  },

  // 🏀 Basketballs
  {
    id: 7,
    name: 'Spalding NBA Official Game Ball',
    price: '$95',
    image: 'https://m.media-amazon.com/images/I/71o5MPDLsHL._AC_SL1500_.jpg'
  },
  {
    id: 8,
    name: 'Wilson Evolution Indoor Ball',
    price: '$75',
    image: 'https://m.media-amazon.com/images/I/71xvbKHt9IL._AC_SL1500_.jpg'
  },
  {
    id: 9,
    name: 'Nike Elite Tournament Ball',
    price: '$85',
    image: 'https://m.media-amazon.com/images/I/81x1R3kA2XL._AC_SL1500_.jpg'
  },
  {
    id: 10,
    name: 'Molten BGGX Indoor Basketball',
    price: '$70',
    image: 'https://www.basketballstore.net/images/detailed/40/BGM7X_main.jpg'
  },
  {
    id: 11,
    name: 'Wilson NCAA Replica Game Ball',
    price: '$60',
    image: 'https://m.media-amazon.com/images/I/81pq3olJ6-L._AC_SL1500_.jpg'
  },
  {
    id: 12,
    name: 'Spalding Street Outdoor Ball',
    price: '$45',
    image: 'https://m.media-amazon.com/images/I/91SmYxksmPL._AC_SL1500_.jpg'
  }
];

const Balls = () => {
  return (
    <div className="balls-container">
      <h2>Football & Basketball Balls</h2>
      <div className="balls-grid">
        {balls.map((item) => (
          <div key={item.id} className="ball-card">
            <img src={item.image} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Balls;
