import React from 'react';
import './Accessoires.css';

const accessoires = [
  // âš½ Football Accessories
  {
    id: 1,
    name: 'Nike Goalkeeper Gloves',
    price: '$45',
    image: 'https://m.media-amazon.com/images/I/81YZhH7qCEL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 2,
    name: 'Shin Guards Adidas',
    price: '$18',
    image: 'https://m.media-amazon.com/images/I/51ldY61JZvL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 3,
    name: 'Football Pump Kit',
    price: '$12',
    image: 'https://m.media-amazon.com/images/I/61oKo+5UBPL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 4,
    name: 'Captain Armband',
    price: '$8',
    image: 'https://m.media-amazon.com/images/I/61X3dQNjGAL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 5,
    name: 'Training Cone Set',
    price: '$20',
    image: 'https://m.media-amazon.com/images/I/71pO9KXonhL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 6,
    name: 'Football Bag',
    price: '$28',
    image: 'https://m.media-amazon.com/images/I/71v10AeQz3L._AC_UF894,1000_QL80_.jpg'
  },

  // ðŸ€ Basketball Accessories
  {
    id: 7,
    name: 'Spalding Shooting Sleeve',
    price: '$15',
    image: 'https://m.media-amazon.com/images/I/51MvnB35IrL._AC_UF1000,1000_QL80_.jpg'
  },
  {
    id: 8,
    name: 'Nike Headband',
    price: '$10',
    image: 'https://m.media-amazon.com/images/I/61hO8DJI7mL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 9,
    name: 'Wristbands Set',
    price: '$9',
    image: 'https://m.media-amazon.com/images/I/61aINzr4nzL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 10,
    name: 'Basketball Net',
    price: '$14',
    image: 'https://m.media-amazon.com/images/I/81p5lxQ1uFL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 11,
    name: 'Mini Hoop Set',
    price: '$22',
    image: 'https://m.media-amazon.com/images/I/71vRJDTuVLL._AC_UF894,1000_QL80_.jpg'
  },
  {
    id: 12,
    name: 'Basketball Bag',
    price: '$30',
    image: 'https://m.media-amazon.com/images/I/61rW6N7DUbL._AC_UF894,1000_QL80_.jpg'
  }
];

const Accessoires = () => {
  return (
    <div className="accessoires-container">
      <h2>Football & Basketball Accessories</h2>
      <div className="accessoires-grid">
        {accessoires.map((item) => (
          <div key={item.id} className="accessoire-card">
            <img src={item.image} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Accessoires;
