    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 6,
    name: 'PSG Jordan 23/24',
    price: '$92',
    image: 'https://images.prodirectsport.com/ProductImages/Main/1002400_Main_Thumb_1677026.jpg',
    sizes: ['S', 'M', 'L', 'XL']
  },

  // ðŸ€ Basketball T-Shirts
  {
    id: 7,
    name: 'Los Angeles Lakers Jersey',
    price: '$110',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRq1WZKOH1D4MJhDqAsFlRyoaM1QhiIaMjPRw&s',
    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 8,
    name: 'Golden State Warriors Jersey',
    price: '$105',
    image: 'https://images.footballfanatics.com/golden-state-warriors/unisex-nike-stephen-curry-black-golden-state-warriors-swingman-badge-player-jersey-city-edition_pi5202000_ff_5202680-898016d5c53d4c7303a4_full.jpg?_hv=2&w=400',
    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 9,
    name: 'Chicago Bulls Jersey',
    price: '$100',
    image: 'https://clarkstreetsports.com/cdn/shop/files/mitchell-ness-adult-jersey-dennis-rodman-chicago-bulls-mitchell-ness-nba-black-pinstripe-throwback-swingman-jersey-29951000707151_575x639_8579c35d-8cb6-4faa-8bfa-b1d37981f247.jpg?v=1744664467',
    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 10,
    name: 'Brooklyn Nets Jersey',
    price: '$98',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHlGH0i1XoX1thHWT4Bs5ihnzssDXE3rlqDA&s',
    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 11,
    name: 'Boston Celtics Jersey',
    price: '$102',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdlZlU5h4C08nNtwgIcM4ye73dFJQBengRFQ&s',
    sizes: ['S', 'M', 'L', 'XL']
  },
  {
    id: 12,
    name: 'Milwaukee Bucks Jersey',
    price: '$99',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ3mGKCUhVtjwBW1bcnmsg8nhiX-MH191u88Q&s',
    sizes: ['S', 'M', 'L', 'XL'] 
  }
];

const Tshirts = () => {
  return (
    <div className="tshirts-container">
      <h2>Football & Basketball T-Shirts</h2>
      <div className="tshirts-grid">
        {tshirts.map((item) => (
          <div key={item.id} className="tshirt-card">
            <img src={item.image} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tshirts;
