import React from 'react';
import './Tshirts.css';

const tshirts = [
  // ⚽ Football T-Shirts
  {
    id: 1,
    name: 'Real Madrid Home 23/24',
    price: '$90',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRPWCoQ9sx6JmaU5laTMlOGLAViGfYIQL-pVg&s'
  },
  {
    id: 2,
    name: 'FC Barcelona Away 23/24',
    price: '$85',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFiRsYRuqtc_9ha1fbYD4ct9BnDZy9NLBz2Q&s'
  },
  {
    id: 3,
    name: ' Ac Milan Home 23/24',
    price: '$88',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRp2dHLc_BHyIlVBZ7vhDIou1L0WEeXDjgI0w&s'
  },
  {
    id: 4,
    name: 'Arsenal Away 23/24',
    price: '$87',
    image: 'https://www.stripe3.com/cdn/shop/files/HR6927_1_APPAREL_Photography_FrontView_grey.jpg?v=1698268736'
  },
  {
    id: 5,
    name: 'Juventus Home 23/24',
    price: '$82',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdp4eoaln8Auw06UJbml0WkfdSYZo2y4Nraw&s'
  },
  {
    id: 6,
    name: 'PSG Jordan 23/24',
    price: '$92',
    image: 'https://images.prodirectsport.com/ProductImages/Main/1002400_Main_Thumb_1677026.jpg'
  },

  // 🏀 Basketball T-Shirts
  {
    id: 7,
    name: 'Los Angeles Lakers Jersey',
    price: '$110',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRq1WZKOH1D4MJhDqAsFlRyoaM1QhiIaMjPRw&s'
  },
  {
    id: 8,
    name: 'Golden State Warriors Jersey',
    price: '$105',
    image: 'https://images.footballfanatics.com/golden-state-warriors/unisex-nike-stephen-curry-black-golden-state-warriors-swingman-badge-player-jersey-city-edition_pi5202000_ff_5202680-898016d5c53d4c7303a4_full.jpg?_hv=2&w=400'
  },
  {
    id: 9,
    name: 'Chicago Bulls Jersey',
    price: '$100',
    image: 'https://clarkstreetsports.com/cdn/shop/files/mitchell-ness-adult-jersey-dennis-rodman-chicago-bulls-mitchell-ness-nba-black-pinstripe-throwback-swingman-jersey-29951000707151_575x639_8579c35d-8cb6-4faa-8bfa-b1d37981f247.jpg?v=1744664467'
  },
  {
    id: 10,
    name: 'Brooklyn Nets Jersey',
    price: '$98',
    image: 'https://images.footballfanatics.com/brooklyn-nets/mens-nike-kevin-durant-black-brooklyn-nets-swingman-jersey_ss4_p-12002621+u-pmhh7hmzmjr4bucjgjhd+v-6uvlchsrkb7a5y3ljo0e.jpg'
  },
  {
    id: 11,
    name: 'Boston Celtics Jersey',
    price: '$102',
    image: 'https://images.footballfanatics.com/boston-celtics/mens-nike-jayson-tatum-green-boston-celtics-swingman-jersey_ss4_p-12002623+u-h4k2cjy9bfm81cuut8jz+v-lrn2tv9db9zfsn7k6fql.jpg'
  },
  {
    id: 12,
    name: 'Milwaukee Bucks Jersey',
    price: '$99',
    image: 'https://images.footballfanatics.com/milwaukee-bucks/mens-nike-giannis-antetokounmpo-green-milwaukee-bucks-swingman-jersey_ss4_p-12002610+u-qay6pw6fkwv0y4yyz0e3+v-mjdvxsckvvldqlybqfp7.jpg'
  }
];

const Tshirts = () => {
  return (
    <div className="tshirts-container">
      <h2>Football & Basketball T-Shirts</h2>
      <div className="tshirts-grid">
        {tshirts.map((item) => (
          <div key={item.id} className="tshirt-card">
            <img src={item.image} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tshirts;
