import React from 'react';
import './Home.css';

const featuredProducts = [
  {
    id: 1,
    name: 'Nike Mercurial Superfly',
    price: '$120',
    image: 'https://static.nike.com/a/images/t_PDP_1280_v1/f_auto,q_auto:eco/23dbe3e3-35f0-4a49-b377-70b136a0ac92/mercurial-superfly-9-elite-fg-football-boot-BJjVVD.png'
  },
  {
    id: 2,
    name: 'Adidas Harden Vol. 7',
    price: '$150',
    image: 'https://assets.adidas.com/images/w_600,f_auto,q_auto/71242ac3de1b4b95a70faef600e9f616_9366/Harden_Vol.7_Basketball_Shoes_Black_IF5616_01_standard.jpg'
  },
  {
    id: 3,
    name: 'Puma Future Z',
    price: '$100',
    image: 'https://images.puma.com/image/upload/f_auto,q_auto,b_rgb:fafafa,w_450,h_450/global/106989/01/sv01/fnd/PNA/fmt/png'
  },
  {
    id: 4,
    name: 'Jordan Luka 2',
    price: '$145',
    image: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/69ff6ed6-9582-4baf-a5e4-19a705c3e515/luka-2-pf-basketball-shoes-dmlcLJ.png'
  }
];

const Home = () => {
  return (
    <div className="home-container">
      <h1>Welcome to Our Sports Shop</h1>

      {/* ...other homepage sections */}

      <h2 style={{ marginTop: '50px' }}>Featured Products</h2>
      <div className="featured-grid">
        {featuredProducts.map((item) => (
          <div key={item.id} className="product-card">
            <img src={item.image} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
